mapper.insert:
INSERT INTO student(
name, no, gender, phone,
id_card, is_college, address, degree, graduation_date, clazz_id,
create_time, update_time
)
VALUES (
#{name}, #{no}, #{gender}, #{phone},
#{idCard}, #{isCollege}, #{address}, #{degree}, #{graduationDate}, #{clazzId},
#{createTime}, #{updateTime}
)

mapper.select:
SELECT s.*, c.name as clazzName FROM student s LEFT JOIN clazz c ON s.clazz_id = c.id
AND s.name LIKE CONCAT('%', #{name}, '%')
AND s.degree = #{degree}
AND s.clazz_id = #{clazzId}
ORDER BY s.create_time DESC
